import axios from "axios";
import React, { Component } from "react";
import userHomePage from "../userHomePage/userHomePage";
import { BrowserRouter as Router, Route, Link} from "react-router-dom";

class booksForCategories extends Component{
    constructor(){
        super();

            this.state = {
                books:[]
            }

    }

    componentDidMount=()=>{
        axios.get("http://localhost:3000/categories/"+this.props.match.params.id).then((Result)=>{
            this.setState({books: Result.data})
    })
    }

    render(){
        return(<div>
            <div>
            {this.state.books.map((e)=>{
                                        let id = e.bookShopeID;
                return(
                <div>
 <Link to={{pathname: `/libraryDetails/`+id, query: {id}}} >
<span>
<h1>{e.bookName}</h1>
</span>
    </Link>
                    <h1>{e.category}</h1>
   
<h1>{e.publisher}</h1>

                   
                    <h1>{e.author}</h1>
                </div>)
            })}
           </div>

        </div>)
    }
}
export default booksForCategories;
