import React, { Component } from "react";
import "./Style.css"
class signIn extends Component{

  constructor(props){
    super(props)
    this.state={
        email: "",
        password: ""
    }
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(e){
    e.preventDefault();
    const {email, password} = this.state;
    console.log(email, password);
    fetch("http://localhost:3000/loginUser",{
        method: "POST",
        crossDomain: true,
        headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
            "Access-control-Allow-Origin": "*",
        },
        body: JSON.stringify({
            email,
            password,
        })
    }).then((res) => res.json()).then((data) => {
        console.log(data, "userSginIN")
    })
}

    render(){
        return(
            <div className="signIn">
                <div className="split left">
                <div class="text-center">
                  <img className="img"
                  src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/lotus.webp" alt="logo"/>
                  <h4 class="mt-1 mb-5 pb-1">We are The Lotus Team</h4>
                </div>
               <form onSubmit={this.handleSubmit}>
                <h3>Sign In</h3>
                <div className="mb-3">
                    <label>Email address</label>
                    <input type="email"
                    className="form-control" 
                    placeholder="Enter email"
                    onChange={(e)=>this.setState({email: e.target.value})}/>
                </div>

                <div className="mb-3">
                    <label>Password</label>
                    <input type="password" 
                    className="form-control" 
                    placeholder="Enter password"
                    onChange={(e)=>this.setState({password: e.target.value})}/>
                </div>

                <div className="mb-3">
                    <div className="custom-control custom -checkbox">
                        <input type="checkbox" 
                        className="custom-control-input" 
                        id="customCheck1"/>
                        <label className="custom-control-label" 
                        htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>

                <div className="d-grid">
                    <button type="submit" 
                    className="btn btn-primary">sign in</button>
                </div>
            </form>
            </div>
            <div className="split right">
            <img className="img"
                  src="https://images.unsplash.com/photo-1576872381149-7847515ce5d8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8M3x8fGVufDB8fHx8&w=1000&q=80" alt="logo"/> 
            </div>  
            </div>
        )
    }
}
export default signIn;
