import React, { Component } from "react";
import axios from "axios";
import { BrowserRouter as Router, Route, Link} from "react-router-dom";

class userHomePage extends Component{
    constructor(){
        super();

            this.state = {
                books:[]

            }
            axios.get("http://localhost:3000/books").then((Result)=>{
                this.setState({books: Result.data})
        })

    }
    updateinputText = (event) => {
        this.props.setInputValue(event.target.value);
      };
    
    render(){
        console.log(this.state.books)
        return(
            <div>
          <input type="text"        placeholder="Search"
          value={this.props.searchInput}
          onChange={this.updateinputText}
          ></input>
                
                {this.state.books.map((e)=>{
                if(e.bookName.includes(this.props.searchInput))
                return(<div>
                <h1>{e.bookName}</h1>
                <h1>{e.category}</h1>
                <h1>{e.publisher}</h1>
                <h1>{e.author}</h1>
                <Link to={{pathname: `/libraryDetails/`+e.bookShopeID}} >
<span>
    See libraryDetails
</span>
    </Link> 
                </div>)
            })}</div>
        )
    }
}
export default userHomePage;
